mapper.select:
SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_EMAIL = #{email}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_NICKNAME = #{nickname}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*) FROM "AUTH_KEY"
WHERE EMAIL = #{email}
AND AUTH_CODE = #{inputKey}

SELECT COUNT(*) FROM "MEMBER"
WHERE MEMBER_PHONE = #{phone}
AND MEMBER_DEL_FL = 'N'

SELECT COUNT(*) FROM "AUTH_KEY"
WHERE PHONE = #{phone}
AND AUTH_CODE = #{inputKey}
