mapper.insert:
INSERT INTO "BOARD_IMG" SELECT SEQ_BOARD_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.boardNo} BOARD_NO, #{img.boardImagePath} BOARD_IMG_PATH, #{img.boardImageReName} BOARD_IMG_RENAME, #{img.boardImageOriginal} BOARD_IMG_ORIGINAL, #{img.boardImageOrder} BOARD_IMG_ORDER FROM DUAL
) A

mapper.update:
UPDATE "BOARD" SET
BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM "BOARD_IMG"
WHERE BOARD_NO = #{boardNo}
AND BOARD_IMG_ORDER IN ( ${deleteList} )

mapper.update:
UPDATE "BOARD_IMG" SET
BOARD_IMG_PATH = #{boardImagePath},
BOARD_IMG_ORIGINAL = #{boardImageOriginal},
BOARD_IMG_RENAME = #{boardImageReName}
WHERE BOARD_NO = #{boardNo}
AND BOARD_IMG_ORDER = #{boardImageOrder}

UPDATE "BOARD" SET
BOARD_DEL_FL = 'Y'
WHERE BOARD_TYPE = #{boardType}
AND BOARD_NO = #{boardNo}
