mapper.select:
SELECT CAMP_IMG_NO, CAMP_NO, CAMP_IMG_PATH, CAMP_IMG_RENAME, CAMP_IMG_ORIGINAL, CAMP_IMG_ORDER
FROM "CAMPING_SITE_IMG"
WHERE CAMP_NO = #{campNo}

SELECT CAMP_CEO_IMG_NO, CAMP_NO, CAMP_CEO_IMG_PATH, CAMP_CEO_IMG_RENAME, CAMP_CEO_IMG_ORIGINAL
FROM "CAMPING_CEO_IMG"
WHERE CAMP_NO = #{campNo}

SELECT COUNT(*)
FROM CAMPING_STAR
WHERE CAMP_NO = #{campNo}
AND MEMBER_NO = #{memberNo}

SELECT COUNTING_STAR
FROM CAMPING_STAR
WHERE CAMP_NO = #{campNo}
AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE CAMPING_STAR SET COUNTING_STAR = #{starNo}
WHERE CAMP_NO = #{campNo}
AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM "WISH_LIST"
WHERE CAMP_NO = #{campNo}
AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO "CAMPING_CEO_IMG" SELECT SEQ_CAMP_CEO_IMG_NO.NEXTVAL, A.* FROM(
SELECT #{img.campNo} CAMP_NO, #{img.campCeoImagePath} CAMP_CEO_IMG_PATH, #{img.campCeoImageReName} CAMP_CEO_IMG_RENAME, #{img.campCeoImageOriginal} CAMP_CEO_IMG_ORIGINAL FROM DUAL
) A
