mapper.insert:
INSERT INTO "ROOM_IMG" SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.productNo} PRODUCT_NO, #{img.imgPath} IMG_PATH, #{img.imgRename} IMG_RENAME, #{img.imgOriginal} IMG_ORIGINAL, #{img.imgOrder} IMG_ORDER FROM DUAL
) A

mapper.delete:
DELETE FROM "ROOM_IMG"
WHERE PRODUCT_NO = #{productNo}
AND IMG_ORDER IN (${deleteList})

mapper.update:
UPDATE "ROOM_IMG" SET
IMG_PATH = #{imgPath},
IMG_ORIGINAL = #{imgOriginal},
IMG_RENAME = #{imgRename}
WHERE PRODUCT_NO = #{productNo}
AND IMG_ORDER = #{imgOrder}
