mapper.select:
	SELECT ITEM_IMG_NO, ITEM_NO, ITEM_IMG_PATH
	FROM ITEM_IMG
 	WHERE ITEM_NO = #{itemNo}

mapper.insert:
INSERT INTO "ITEM_IMG" SELECT SEQ_ITEM_IMG_NUM.NEXTVAL, A.* FROM (
SELECT #{img.itemNo} ITEM_NO, #{img.itemImgPath} ITEM_IMG_PATH, #{img.itemImgRename} ITEM_IMG_RENAME, #{img.itemImgOriginal} ITEM_IMG_ORIGINAL, #{img.itemImgOrder} ITEM_IMG_ORDER FROM DUAL
) A

mapper.update:
UPDATE "ITEM_IMG" SET
ITEM_IMG_PATH = #{itemImgPath},
ITEM_IMG_ORIGINAL = #{itemImgOriginal},
ITEM_IMG_RENAME = #{itemImgRename}
WHERE ITEM_NO = #{itemNo}
AND ITEM_IMG_ORDER = #{itemImgOrder}
