mapper.select:
SELECT *
FROM QNA
WHERE MEMBER_NO = #{memberNo}

SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, B_READ_COUNT, BOARD_TYPE, B_CREATE_DATE,
(SELECT COUNT(*) FROM "COMMENT" C WHERE C.BOARD_NO = B.BOARD_NO AND COMMENT_DEL_FL = 'N') COMMENT_COUNT, (SELECT COUNT(*) FROM BOARD_LIKE L WHERE L.BOARD_NO = B.BOARD_NO) LIKE_COUNT
FROM "BOARD" B JOIN "MEMBER" USING(MEMBER_NO) WHERE BOARD_DEL_FL = 'N' AND MEMBER_NO = #{memberNo}

SELECT MEMBER_NO
	FROM "MEMBER"
	WHERE MEMBER_NO = #{memberNo}

