mapper.select:
SELECT COUNT(*) FROM CART_ITEM
WHERE CART_NO = #{cartNo}
AND ITEM_NO = #{itemNo}
AND RESERVATION_NO = #{reservationNo}

SELECT CART_NO
FROM CART
WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE CART_ITEM
SET ITEM_QUANTITY = #{itemQuantity}
WHERE CART_ITEM_NO = #{cartItemNo}

UPDATE CART_ITEM
SET RESERVATION_NO = #{reservationNo}
WHERE CART_ITEM_NO = #{cartItemNo}
